package com.training.array;

public class OneDimArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int marks[] = new int[4];
		marks[0] = 90;
		marks[1] = 80;
		marks[2] = 70;
		marks[3] = 90;
		for(int i=0; i<marks.length; i++) {
			System.out.println(marks[i]);
		}

	}

}
