package com.training.demo;

public class SwitchDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10, y = 20;
		switch(x+y) {
		case 30:
			System.out.println("sum is"+(x+y));
			break;
			
		case 40:
			System.out.println("sum is"+(x+y));
			break;
	
		case 50:
			System.out.println("sum is"+(x+y));
			break;
		
		default:
			System.out.println("no match");
		}


	}

}
